int main(void)
{
	// Setup hardware and call backs. This will turn on
	// interrupts.
	initialise_hardware();

	// Show the splash screen message. Returns when display
	// is complete.
	start_screen();

	// Loop forever and continuously play the game.
	while (1)
	{
		new_game();
		play_game();
		handle_game_over();
	}
}

void initialise_hardware(void)
{
	ledmatrix_setup();
	init_button_interrupts();
