void new_game(void)
{
	// Clear the serial terminal
	clear_terminal();

	// Initialise the game and display
	initialise_game();

	// Clear a button push or serial input if any are waiting
	// (The cast to void means the return value is ignored.)
	(void)button_pushed();
	clear_serial_input_buffer();
}

void play_game(void)
{
	uint32_t last_flash_time, current_time;
	int8_t btn; // The button pushed

	last_flash_time = get_current_time();

	// We play the game until it's over
	while (!is_game_over())
	{
		// We need to check if any button has been pushed, this will be
		// NO_BUTTON_PUSHED if no button has been pushed
		// Checkout the function comment in buttons.h and the implementation
		// in buttons.c.
		btn = button_pushed();

		// Terminal inputs (wasd/WASD)
		char serial_input = -1;
		if (serial_input_available())
		{
			serial_input = fgetc(stdin);
		}

		// Consolidating movement logic for both button and serial inputs
		int move_x = 0, move_y = 0;
		switch (btn)
		{
		case BUTTON0_PUSHED:
			move_x = 1;
			break; // Move right
		case BUTTON1_PUSHED:
			move_y = -1;
			break; // Move up
		case BUTTON2_PUSHED:
			move_y = 1;
			break; // Move down
		case BUTTON3_PUSHED:
			move_x = -1;
			break; // Move left
		}

		switch (serial_input)
		{
		case 'w':
		case 'W':
			move_y = 1;
			break; // Move up
		case 'a':
		case 'A':
			move_x = -1;
			break; // Move left
		case 's':
		case 'S':
			move_y = -1;
			break; // Move down
		case 'd':
		case 'D':
			move_x = 1;
			break; // Move right
		case 'f':
		case 'F':
			// New: Handle firing
			handle_firing();
			break;
		case 'c':
		case 'C':
			// Cheating
			cheating();
			break;
		case '1':
			// Segment timer 1
			break;
		case '2':
			// Segment timer 2
			break;
		case '3':
			// Segment timer 3
			break;
		case '4':
			// Segment timer 4
			break;
		case '5':
			// Segment timer 5
			break;
		}

		if (move_x != 0 || move_y != 0)
		{
			move_cursor(move_x, move_y);
			// see move_cursor(...) in game.c
			// Reset flashing time
			last_flash_time = current_time;
		}

		current_time = get_current_time();
		if (current_time >= last_flash_time + 200)
		{
			// 200ms (0.2 second) has passed since the last time we advance the
			// notes here, so update the advance the notes
			flash_cursor();

			// Update the most recent time the notes were advance
			last_flash_time = current_time;
		}
	}
	// We get here if the game is over.
}

